import React, { useState, useEffect } from 'react'
import { NativeSelect, FormControl, Container } from '@material-ui/core';
import styles from './CountryPicker.module.css';
import { getCovidData } from '../../api';
import Accordion from '../Accordion/Accordion'
import Select from '../AutoComplete'
import { TempleBuddhistOutlined } from '@mui/icons-material';
import Grid from '@mui/material/Grid';
import Paper from '@mui/material/Paper';
import { DataGrid } from '@mui/x-data-grid';
import Box from '@mui/material/Box';
import Chart from '../Chart/Chart'

const CountryPicker = ({ handleCountryChange }) => {
    const [stateMap, setStateMap] = useState({});
    const [states, setStates] = useState([]);
    const [selectedSno, setSelectedSno] = useState('');

    useEffect(() => {
        const getStateData = async () => {
            let data = await getCovidData();
            let tempMap = {}
            let tempStates = [{ label: "India", sno: "37" }]
            data.forEach(state => {
                if (!tempMap[state.sno]) {
                    tempMap[state.sno] = state
                    if (state.state_name)
                        tempStates.push({ label: state.state_name, sno: state.sno })
                }
            })
            setStateMap(tempMap);
            setStates(tempStates);
        }
        getStateData();
    }, [])


    const handleChange = (event, value) => {
        setSelectedSno(value.sno)
    }

    const columns = [
        { field: 'id', headerName: 'ID', width: 90 },
        {
            field: 'firstName',
            headerName: 'First name',
            width: 150,
            editable: true,
        },
        {
            field: 'lastName',
            headerName: 'Last name',
            width: 150,
            editable: true,
        },
        {
            field: 'age',
            headerName: 'Age',
            type: 'number',
            width: 110,
            editable: true,
        },
        {
            field: 'fullName',
            headerName: 'Full name',
            description: 'This column has a value getter and is not sortable.',
            sortable: false,
            width: 160,
            valueGetter: (params) =>
                `${params.row.firstName || ''} ${params.row.lastName || ''}`,
        },
    ];

    const rows = [
        { id: 1, lastName: 'Snow', firstName: 'Jon', age: 35 },
        { id: 2, lastName: 'Lannister', firstName: 'Cersei', age: 42 },
        { id: 3, lastName: 'Lannister', firstName: 'Jaime', age: 45 },
        { id: 4, lastName: 'Stark', firstName: 'Arya', age: 16 },
        { id: 5, lastName: 'Targaryen', firstName: 'Daenerys', age: null },
        { id: 6, lastName: 'Melisandre', firstName: null, age: 150 },
        { id: 7, lastName: 'Clifford', firstName: 'Ferrara', age: 44 },
        { id: 8, lastName: 'Frances', firstName: 'Rossini', age: 36 },
        { id: 9, lastName: 'Roxie', firstName: 'Harvey', age: 65 },
    ];
    return (
        <>
            <Select data={states} onChange={handleChange} />
            <Container>
                <Grid sx={{ flexGrow: 1 }} container spacing={2}>
                    <Grid item xs={6}>
                        <Box component="div" style={{ height: 400, width: '100%' }}>
                            <DataGrid
                                rows={rows}
                                columns={columns}
                                pageSize={5}
                                rowsPerPageOptions={[5]}
                                checkboxSelection
                                disableSelectionOnClick
                            />
                        </Box>
                    </Grid>
                    <Grid item xs={6}>
                        <Chart data={{ confirmed: stateMap[selectedSno].new_positive | 0, recovered: stateMap[selectedSno].new_cured | 0, deaths: stateMap[selectedSno].new_death | 0 }} country={'India'} />
                    </Grid>
                </Grid>
            </Container>

            {/* {stateMap} */}

        </>
        // <FormControl className={styles.formControl}>
        //     <NativeSelect defaultValue="" onChange={(e) => handleCountryChange(e.target.value)}>
        //         <option value="">Global</option>
        //         {stateData.map((state, id) => <option value={state.state_bane} key={id}>{state.state_name}</option>)}
        //     </NativeSelect>
        // </FormControl>
        // <Accordion data={stateData} />
    )

}

export default CountryPicker;
