import React, { useState, useEffect } from 'react';
import { fetchDailyData } from '../../api';
import { Line, Bar } from 'react-chartjs-2';
import styles from './Chart.module.css';

const Chart = ({ data: { confirmed, recovered, deaths }, state }) => {

    const barChart = (
        confirmed ? (
            <Bar
                data={{
                    labels: ['Positive', 'Cured', 'Deaths'],
                    datasets: [{
                        label: 'People',
                        backgroundColor: ['rgba(255, 0, 0, 0.7)', 'rgba(0, 255, 0, 0.7)', 'rgba(0, 0, 0, 0.7)'],
                        data: [confirmed, recovered, deaths]
                    }]
                }}
                options={{
                    legend: { display: false },
                    title: { display: true, text: `Current State in ${state}`, position: 'bottom' }
                }}
            />
        ) : null
    )


    return (
        <div className={styles.container}>
            {barChart}
        </div>
    )


}

export default Chart;
