import React, { Component } from 'react';
import { Cards, Chart, CountryPicker } from './components';
import styles from './App.module.css';
import { getCovidData } from './api';
import image from './images/image.png';
class App extends Component {

    state = {
        data: {},
        country: "India",
        global_positives: 0,
        global_cured: 0,
        global_death: 0
    }

    async componentDidMount() {
        const fetchedData = await getCovidData();
        this.setState({ data: fetchedData });
        this.setState({ confirmed: fetchedData[36].new_positive })
        this.setState({ recovered: fetchedData[36].new_cured })
        this.setState({ death: fetchedData[36].new_death })
    }

    // handleStateChange = (state) => {
    //     const fetchedData = await fetchData(country);
    //     this.setState({ data: fetchedData, country: country });
    // }

    render() {
        const { data, confirmed, recovered, death } = this.state;
        return (
            <div className={styles.container}>
                <img src={image} alt="CoronaVirus" className={styles.image} />
                {console.log(data)}
                <Cards data={{ confirmed: confirmed, recovered: recovered, death: death }} />
                {/* <CountryPicker handleCountryChange={this.handleCountryChange} /> */}

                {/* <Chart data={data} country={country} /> */}
            </div>
        )
    }
}

export default App;
