import React, { useEffect, useState } from 'react';
import styles from './CountryPicker.module.css';
import Grid from '@mui/material/Grid';
import Paper from '@mui/material/Paper';
import Chart from '../Chart/Chart'
import Table from '@material-ui/core/Table';
import TableCell from '@material-ui/core/TableCell';
import TableRow from '@material-ui/core/TableRow';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import TableContainer from "@material-ui/core/TableContainer";
import MuiTableHead from "@material-ui/core/TableHead";
import { Container, Typography } from '@material-ui/core';

const useStyles = makeStyles({
    table: {
        width: "100%",
    },
});
const TableHead = withStyles((theme) => ({
    root: {
        backgroundColor: "orange"
    }
}))(MuiTableHead);

const TableHeaderCell = withStyles((theme) => ({
    root: {
        color: "white"
    }
}))(TableCell);

const CountryPicker = ({ data }) => {

    const classes = useStyles();
    const [positives, setPositives] = useState(data?.new_positive - data?.positive);
    const [cured, setCured] = useState(data?.new_cured - data?.cured);
    const [deaths, setDeaths] = useState(data?.new_deaths - data?.deaths);

    useEffect(() => {
        setPositives(data?.new_positive - data?.positive);
        setCured(data?.new_cured - data?.cured);
        setDeaths(data?.new_deaths - data?.deaths)
    }, [data])
    return (
        <Container className={styles.container}>
            <Grid container spacing={2}>
                <Grid item xs={6}>
                    <Chart data={{ confirmed: data?.new_positive, recovered: data?.new_cured, deaths: data?.new_death }} country={data?.state_name} />
                </Grid>
                <Grid item xs={6}>
                    <div className="App">
                        <div className='table-container'>
                            <TableContainer component={Paper}>
                                <Table className={classes.table} >
                                    <TableHead>
                                        <TableRow>
                                            <TableHeaderCell>Changes Since Yesterday</TableHeaderCell>
                                            <TableHeaderCell>Count</TableHeaderCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableRow className='row-style'>
                                        <TableCell variant="head">Positives</TableCell>
                                        <TableCell><Typography style={{ color: positives >= 0 ? "green" : "red", fontWeight: "bold" }}>{Math.abs(positives)}</Typography></TableCell>
                                    </TableRow>
                                    <TableRow className='row-style'>
                                        <TableCell variant="head">Cured</TableCell>
                                        <TableCell>{data?.new_cured - data?.cured}</TableCell>
                                    </TableRow>
                                    <TableRow className='row-style'>
                                        <TableCell variant="head">Deaths</TableCell>
                                        <TableCell>{data?.new_death - data?.death}</TableCell>
                                    </TableRow>
                                </Table>
                            </TableContainer>
                        </div>
                    </div>
                </Grid>
            </Grid>
        </Container >
    )
}

export default CountryPicker;
