import React, { useState, useEffect } from 'react'
import { NativeSelect, FormControl, Container } from '@material-ui/core';
import styles from './CountryPicker.module.css';
import { getCovidData } from '../../api';
import Accordion from '../Accordion/Accordion'
import Select from '../AutoComplete'
import { TempleBuddhistOutlined } from '@mui/icons-material';
import Grid from '@mui/material/Grid';
import Paper from '@mui/material/Paper';
import { DataGrid } from '@mui/x-data-grid';
import Box from '@mui/material/Box';
import Chart from '../Chart/Chart'
import Cards from '../Cards/Cards'
import { Card, CardContent, Typography } from '@material-ui/core';
import Table from '@material-ui/core/Table';
import TableCell from '@material-ui/core/TableCell';
import TableRow from '@material-ui/core/TableRow';
import { makeStyles } from '@material-ui/core/styles';
const useStyles = makeStyles({
    table: {
        width: 650,
    },
});

const CountryPicker = ({ handleCountryChange }) => {
    const [stateMap, setStateMap] = useState({});
    const [states, setStates] = useState([]);
    const [selectedSno, setSelectedSno] = useState();
    const [selectedState, setSelectedState] = useState("India");
    const [confirmed, setConfirmed] = useState(0);
    const [recovered, setRecovered] = useState(0);
    const [death, setDeath] = useState(0);
    const classes = useStyles();


    useEffect(() => {
        const getStateData = async () => {
            let data = await getCovidData();
            let tempMap = {}
            let tempStates = [{ label: "India", sno: "37" }]
            data.forEach(state => {
                if (!tempMap[state.sno]) {
                    tempMap[state.sno] = state
                    if (!state.state_name) {
                        state.state_name = "India"
                    }
                    if (state.state_name)
                        tempStates.push({ label: state.state_name, sno: state.sno })
                }
            })
            setStateMap(tempMap);
            setStates(tempStates);
        }
        getStateData();
    }, [])

    useEffect(() => {
        if (selectedSno) {
            setConfirmed(stateMap[selectedSno].new_positive)
            setRecovered(stateMap[selectedSno].new_cured)
            setDeath(stateMap[selectedSno].new_death)
            setSelectedState(stateMap[selectedSno].state_name)
        }
    }, [selectedSno])


    const handleChange = (event, value) => {
        setSelectedSno(value.sno)
    }

    return (
        <>
            <Cards data={{ confirmed: confirmed, recovered: recovered, deaths: death }} />
            <Select data={states} onChange={handleChange} />
            <Container>
                <Grid container spacing={2}>
                    <Grid item xs={6}>
                        <Chart data={{ confirmed: confirmed, recovered: recovered, deaths: death }} country={selectedState} />
                    </Grid>
                    <Grid item xs={6}>
                        <div className="App">
                            <div className='table-container'>
                                <Table className={classes.table} >
                                    <TableRow className='row-style'>
                                        <TableCell variant="head">Active Cases since yesterday</TableCell>
                                        <TableCell>{selectedSno && (stateMap[selectedSno].new_positive - stateMap[selectedSno].positive)}</TableCell>
                                    </TableRow>
                                    <TableRow className='row-style'>
                                        <TableCell variant="head">Cured since yesterday</TableCell>
                                        <TableCell>{selectedSno && (stateMap[selectedSno].new_cured - stateMap[selectedSno].cured)}</TableCell>
                                    </TableRow>
                                    <TableRow className='row-style'>
                                        <TableCell variant="head">Deaths since yesterday</TableCell>
                                        <TableCell>{selectedSno && (stateMap[selectedSno].new_death - stateMap[selectedSno].death)}</TableCell>
                                    </TableRow>
                                </Table>
                            </div>
                        </div>
                    </Grid>
                </Grid>
            </Container>


        </>

    )


}

export default CountryPicker;
