import React, { Component } from 'react';
import { Cards, Chart, CountryPicker, AutoComplete } from './components';
import styles from './App.module.css';
import { getCovidData } from './api';
import image from './images/image.png';
class App extends Component {

    state = {
        data: {},
        country: "India",
        global: {},
        stateMap: {},
        states: [],
        selectedSno: 0
    }

    async componentDidMount() {
        const fetchedData = await getCovidData();
        this.setState({ data: fetchedData });
        this.setState({ global: fetchedData.pop() })
        let tempMap = {}
        let tempStates = []
        fetchedData.forEach(state => {
            if (!tempMap[state.sno]) {
                tempMap[state.sno] = state
                if (state.state_name)
                    tempStates.push({ label: state.state_name, sno: state.sno });
            }
        })
        this.setState({ stateMap: tempMap, states: tempStates })
    }

    // handleStateChange = (state) => {
    //     const fetchedData = await fetchData(country);
    //     this.setState({ data: fetchedData, country: country });
    // }
    handleChange = (event, value) => {
        this.setState({ selectedSno: value.sno })
    }

    render() {
        const { data, global, stateMap, states, selectedSno } = this.state;
        return (
            <div className={styles.container}>
                <img src={image} alt="CoronaVirus" className={styles.image} />
                {console.log(global, stateMap, states)}
                <Cards data={{ confirmed: global.new_positive, recovered: global.new_cured, death: global.new_death }} />
                <Select data={states} onChange={this.handleChange} />
                <CountryPicker data={stateMap[selectedSno]} />

                {/* <Chart data={data} country={country} /> */}
            </div>
        )
    }
}

export default App;
