import React, { useState, useEffect } from 'react'
import { NativeSelect, FormControl } from '@material-ui/core';
import styles from './CountryPicker.module.css';
import { getCovidData } from '../../api';
import Accordion from '../Accordion/Accordion'
import Select from '../AutoComplete'
import { TempleBuddhistOutlined } from '@mui/icons-material';

const CountryPicker = ({ handleCountryChange }) => {
    const [stateData, setStateData] = useState([]);
    const [states, setStates] = useState([]);
    const [selectedSno, setSelectedSno] = useState('');

    useEffect(() => {
        const getStateData = async () => {
            setStateData(await getCovidData());
        }
        getStateData();
    }, [])

    useEffect(() => {
        let temp = []
        stateData.forEach(state => {
            temp.push({ label: state.state_name, sno: state.sno })
        })
        setStates(temp)
    }, [stateData])

    const handleChange = (event, value) => {
        setSelectedSno(value.sno)
    }

    return (
        <Select data={states} onChange={handleChange} />
        // <FormControl className={styles.formControl}>
        //     <NativeSelect defaultValue="" onChange={(e) => handleCountryChange(e.target.value)}>
        //         <option value="">Global</option>
        //         {stateData.map((state, id) => <option value={state.state_bane} key={id}>{state.state_name}</option>)}
        //     </NativeSelect>
        // </FormControl>
        // <Accordion data={stateData} />
    )

}

export default CountryPicker;
